import React, {useState} from 'react';
import * as Font from 'expo-font';
import { AppLoading } from 'expo';
import { createStackNavigator } from '@react-navigation/stack';
import { createDrawerNavigator } from '@react-navigation/drawer';
import 'react-native-gesture-handler';
import { NavigationContainer } from '@react-navigation/native';
import Home from './screens/home';
import Slangs from './screens/slangs';
import Phrases from './screens/phrases';
import Detail from './screens/detail';

const Drawer = createDrawerNavigator();
const Stack = createStackNavigator();

const createHomeStack = () => {
  return(

      <Stack.Navigator >
       <Stack.Screen name="Home" component={Home}/>
       <Stack.Screen name="Slangs" component={Slangs}/>
       <Stack.Screen name="Phrases" component={Phrases}/>
       <Stack.Screen name="Detail" component={Detail}/>
     </Stack.Navigator>

  );


}




const getFonts = () => {
  return Font.loadAsync(
    {
      'Roboto-regular': require('./assets/fonts/Roboto-Regular.ttf'),
      'Roboto-medium': require('./assets/fonts/Roboto-Medium.ttf'),
      'Roboto-bold': require('./assets/fonts/Roboto-Bold.ttf'),
    }
  );
}









export default function App() {
  const [fontsLoaded, setFontsLoaded] = useState(false);

  // make sure font is loaded before rendering home screen
  if (fontsLoaded) {
    return (

    <NavigationContainer>
      <Drawer.Navigator> 
        <Drawer.Screen name="Home" children={createHomeStack}/>
        <Drawer.Screen name="Slangs" component={Slangs}/>
      </Drawer.Navigator>
    </NavigationContainer>
      // returns the screen in the stack

    );
  } else {
    return (
      <AppLoading 
        startAsync={getFonts} 
        onFinish={() => setFontsLoaded(true)} 
      />
    )
  }

}
